mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.bumen_types as bumenTypes ,yonghu.gangwei_types as gangweiTypes ,yonghu.yonghu_delete as yonghuDelete
FROM richeng a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and a.richeng_richeng like CONCAT('%',#{params.richengRicheng},'%')
and a.richeng_types = #{params.richengTypes}
and UNIX_TIMESTAMP(a.richeng_time) >= UNIX_TIMESTAMP(#{params.richengTimeStart})
and UNIX_TIMESTAMP(a.richeng_time) <= UNIX_TIMESTAMP(#{params.richengTimeEnd})
and a.richeng_text like CONCAT('%',#{params.richengText},'%')
and a.richeng_content like CONCAT('%',#{params.richengContent},'%')
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.bumen_types = #{params.bumenTypes}
and yonghu.gangwei_types = #{params.gangweiTypes}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
